import styled from 'styled-components';

export const StyledHeader = styled.header`
    background-color: #ebfbff;
    padding: 2.2em 0;

    h1 {
        color: ${(props) => (props.active ? 'red' : 'black')};
        background-color: ${({ bg }) => bg};
    }
`;
